<link href='/stylesheets/application.css' rel='stylesheet'>
<style>
  /* START CONTENT FIELD: CSS */
  /* BASE ============================================== */
                body {
                  background: #fff;
                  font-family: 'Open Sans', sans-serif;
                  /*font-weight: 400;*/
                  }
                strong, b, .font_bold, .contact-form {
                  /*font-weight: 900;*/
                  }
                h1, h2, h3, .logo_text {
                  font-family: 'Rock Salt', cursive;
                  font-weight: 400;
                  }
                h4 {
                  opacity:1;
                }
                .site_width {
                  padding-left:3.25em;
                }
                .shadow_text {
                  text-shadow: 1px 3px 8px #000;
                }
                .float_right {
                  float: right;
                }
  /* NAVIGATION ============================================== */
                .site_header {
                  padding: 2em .5em;
                  border:none;
                  float:left;
                  height: 100%;
                  background: #d0660a;
                  position: fixed;
                  z-index: 10;
                  color: #fff;
                  box-shadow: 1px 3px 8px #000;
                }
                .site_header .site_width {
                  padding: 0;
                }
                #nav {
                  display: none;
                  padding: 0 2em;
                }
                .app_nav a {
                  display: block;
                  margin-top: 3em;
                  border-bottom: none;
                  color:#fff;
                }
                #menu_expander, #close_menu {
                  border-bottom: none;
                  font-size: 2.2em;
                }
                #close_menu {
                  display: none;
                  font-size: 2.2em;
                }
  /* MAIN PHOTO ============================================== */
                .home_optional_row_1 {
                  background: url("https://s3.amazonaws.com/lawgical/assets/data/4671/original.jpg") repeat scroll center center / cover  #555A5E;
                  padding: 25em 0;
                  box-shadow: 0px 4px 8px #000;
                  position: relative;
                }
                .home_optional_row_1:before {
                  content: "";
                  position: absolute;
                  top: 0;
                  bottom: 0;
                  width: 100%;
                  z-index: 1;
                  background: rgba(103, 103, 103, 0.5);
                }
                .home_optional_row_1 .site_width {
                  position: relative;
                  z-index: 2;
                }
  /* ABOUT ============================================== */
                .home_feature {
                  background: #fff;
                  padding: 4em 0;
                  /*color: white;*/
                }
                .home_feature .site_width {
                  max-width: 50rem;
                }
                .drop_cap {
                  /*background-color: #813d02;*/
                }
  /* VIDEO ============================================== */
                .home_optional_row_2 {
                  background: inherit;
                  }
                .video_reel {
                  background: #e4b389;
                  padding: 4em 0;
                }  
  /* GEAR ================================================ */
                #gear {
                  padding: 4em 0;
                }
                .toggle_trigger {
                  border-bottom: 2px solid #000;
                  margin-top: 2em;
                }
  /* IMAGES ============================================== */
                .image_array {
                  background: #fff;
                  padding: 4em 0;
                  margin: 0 auto;
                  display: flex;
                  flex-wrap: wrap;
                  align-items: center;
                }
                .image_array .site_width {
                  max-width: 90rem;
                }
                .kelly_pics {
                  max-width: 25%;
                  vertical-align: middle;
                  transition: transform .2s;
                  margin:1em;
                }

  /* LEFTOVERS ============================================== */
                .site_content {
                  background: #fff;
                  padding: 0;
                }
  /* CONTACT ============================================== */
                .home_optional_row_3 {
                  background: #e4b389;
                  padding: 2em 0;
                }
                .home_optional_row_3 .site_width {
                  max-width: 45rem;
                }
                input[type='text'], input[type='email'], input[type='password'], textarea, select {
                  max-width: 45rem;
                  border-radius: 0;
                }
  /* FOOTER ============================================== */
                .site_footer {
                  background: #d0660a;
                  }
  /* BUTTONS ============================================== */
                .btn, .btn:hover {
                  background: #536e7b;
                  border-radius: 0;
                  font-size: 1.2em;
                }
                button, button:hover {
                  padding: 0;
                  background: transparent;
                  border: none;
                  box-shadow: none;
                  font-size: 1.3em
                }
                .contact-form input[type='submit'] {
                  background: #536e7b;
                  color: #fff;
                  border-radius: 0;
                }
                .contact-form input[type='submit']:hover {
                  background: #536e7b;
                  color: #fff;
                }
                .contact-form input[type='submit']:active {
                  background-color: #536e7b;
                  color: rgba(255, 255, 255, 0.53);
                }
  /* MEDIA QUERIES ============================================== */
               @media screen and (max-width: 767px) {
                .home_optional_row_1 {
                  padding: 15em 0;
                }
                .kelly_pics {
                  max-width: 80%;
                }
                h1 {
                  font-size: 2em;
                  line-height: 2em;
                }
                .table_cell {
                    vertical-align: middle;
                  }
               }
  /* END CONTENT FIELD: CSS */
</style>
